// @ts-nocheck
import axios from 'axios';
const { createSlice, createAsyncThunk } = require('@reduxjs/toolkit');

export const fetchPizzas = createAsyncThunk('pizza/fetchPizzasStatus', async (params) => {
  const {
    url,
    replaceSortType,
    replaceSortTypeFirstSymbol,
    categorySort,
    searchParam,
    currentPage,
  } = params;

  const { data } = await axios.get(
    `${url}?p=${currentPage}&l=4&${categorySort}&sortBy=${replaceSortType}&order=${replaceSortTypeFirstSymbol}${searchParam}`,
  );
  return data;
});

const initialState = {
  items: [],
  status: 'Идёт загрузка...',
};

const pizzaSlice = createSlice({
  name: 'pizza',
  initialState,
  reducers: {
    setItems(state, action) {
      state.items = action.payload;
    },
  },
  extraReducers: {
    [fetchPizzas.pending]: (state) => {
      state.status = 'Идёт загрузка...';
    },
    [fetchPizzas.fulfilled]: (state) => {
      state.status = 'Идёт ...';
    },
  },
});

export const { setItems } = pizzaSlice.actions;
export default pizzaSlice.reducer;
