import { getTotalSumCart } from './getTotalSumCart';

export const getCartFromLS = () => {
  const data = localStorage.getItem('cart');
  const items = data ? JSON.parse(data) : [];

  if (items.length) {
    return {
      items,
      totalCount: getTotalSumCart(items),
    };
  }
};
