import React, { useContext, useEffect, useState } from 'react';
import Categories from '../components/Categories';
import Sort from '../components/Sort.jsx';
import PizzaBlock from '../components/PizzaBlock';
import Skeleton from '../components/PizzaBlock/Skeleton';
import { SearchContext } from 'context/SearchContext';

export default function Home() {
  const [items, setItems] = useState([]);
  const { searchValue, setSearchValue } = useContext(SearchContext);
  const [isLoading, setIsLoading] = useState(true);
  const [category, setCategory] = React.useState(0);
  const [sort, setSort] = useState({
    title: 'популярности',
    type: 'rating',
  });

  const fetchData = async () => {
    setIsLoading(true);
    const url = 'https://650ec8a054d18aabfe9976df.mockapi.io/items';
    const replaceSortType = sort.type.replace('+', '');
    const replaceSortTypeFirstSymbol = sort.type.startsWith('+') ? 'asc' : 'desc';
    const categorySort = category > 0 ? `category=${category}` : '';
    await fetch(
      `${url}?${categorySort}&sortBy=${replaceSortType}&order=${replaceSortTypeFirstSymbol}`,
    )
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setItems(data);
        setIsLoading(false);
      });
  };

  const handleSearchItems = (searchValue) => {
    if (searchValue !== ' ') {
      console.log(searchValue);
      [...items].filter((i) => {
        console.log(searchValue);
        i.title.toLowerCase().includes(searchValue.toLowerCase());
      });
    }
    console.log(searchValue);
  };

  useEffect(() => {
    handleSearchItems(searchValue);
  }, [searchValue]);

  useEffect(() => {
    fetchData();
    window.scrollTo(0, 0);
  }, [category, sort]);

  return (
    <div className="container">
      <div className="content__top">
        <Categories category={category} handleCategory={(i) => setCategory(i)} />
        <Sort sort={sort} handleSort={(i) => setSort(i)} />
      </div>
      <h2 className="content__title">Все пиццы</h2>
      <div className="content__items">
        {isLoading
          ? [...new Array(6)].map((_, index) => <Skeleton key={index} />)
          : items.map((item) => <PizzaBlock key={item.id} {...item} />)}
      </div>
    </div>
  );
}
