import axios from 'axios';
import { createSlice, createAsyncThunk, current } from '@reduxjs/toolkit';
import { RootState } from '../store';

interface FetchPizzasState {
  url: string;
  currentPage: string;
  categorySort: string;
  replaceSortType: string;
  replaceSortTypeFirstSymbol: string;
  searchParam: string;
}

export const fetchPizzas = createAsyncThunk(
  'pizza/fetchPizzasStatus',
  async (params: FetchPizzasState) => {
    const {
      url,
      currentPage,
      categorySort,
      replaceSortType,
      replaceSortTypeFirstSymbol,
      searchParam,
    } = params;

    const { data } = await axios.get(
      `${url}?p=${currentPage}&l=4&${categorySort}&sortBy=${replaceSortType}&order=${replaceSortTypeFirstSymbol}${searchParam}`,
    );

    return data;
  },
);

const initialState = {
  items: [],
  status: 'loading',
};

const pizzaSlice = createSlice({
  name: 'pizza',
  initialState,
  reducers: {
    setItems(state, action) {
      state.items = action.payload;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchPizzas.pending, (state) => {
        state.items = [];
        state.status = 'loading';
      })
      .addCase(fetchPizzas.fulfilled, (state, action) => {
        state.items = action.payload;
        state.status = 'success';
      })
      .addCase(fetchPizzas.rejected, (state) => {
        state.items = [];
        state.status = 'error';
      });
  },
});
export const selectPizzaData = (state: RootState) => state.pizza;
export const { setItems } = pizzaSlice.actions;
export default pizzaSlice.reducer;
