import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  isOpen: false,
  title: 'популярности',
  type: 'rating',
};

const modalSlice = createSlice({
  name: 'modal',
  initialState,
  reducers: {
    openSortModal: (state, action) => {
      state.isOpen = true;
      console.log(action);
    },
    closeSortModal: (state, action) => {
      console.log(action);
      state.isOpen = false;
      state.title = action.payload.title;
      state.type = action.payload.type;
    },
  },
});

export const { openSortModal, closeSortModal } = modalSlice.actions;
export default modalSlice.reducer;
