import React, { useEffect, useState } from 'react';
import './scss/app.scss';
import Header from '../src/components/Header.jsx';
import Categories from '../src/components/Categories.jsx';
import Sort from '../src/components/Sort.jsx';
import PizzaBlock from '../src/components/PizzaBlock';
import Skeleton from './components/PizzaBlock/Skeleton';

export default function Home(params) {
  const [items, setItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const fetchData = async () => {
    await fetch('https://650ec8a054d18aabfe9976df.mockapi.io/items')
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setItems(data);
        setIsLoading(false);
      });
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="wrapper">
      <Header />
      <div className="content">
        <div className="container">
          <div className="content__top">
            <Categories />
            <Sort />
          </div>
          <h2 className="content__title">Все пиццы</h2>
          <div className="content__items">
            {isLoading
              ? [...new Array(6)].map((_) => <Skeleton key={_} />)
              : items.map((item) => <PizzaBlock key={item.id} {...item} />)}
          </div>
        </div>
      </div>
    </div>
  );
}
