import React, { useState } from 'react';
import { useSelector } from 'react-redux';

export default function Sort({ sort, handleSort }) {
  const dispatch = useSelector();
  const sortItems = [
    { id: 1, title: 'популярности (высокая оценка)', type: '+rating' },
    { id: 2, title: 'популярности (низкая оценка)', type: 'rating' },
    { id: 3, title: 'цене (по возрастанию)', type: '+price' },
    { id: 4, title: 'цене (по убыванию)', type: 'price' },
    { id: 5, title: 'алфавиту (а-я)', type: '+title' },
    { id: 6, title: 'алфавиту (я-а)', type: 'title' },
  ];
  const [isOpenSort, setIsOpenSort] = useState(false);

  const closeSortWindow = (i) => {
    handleSort(i);
    setIsOpenSort(false);
  };
  return (
    <div className="sort">
      <div className="sort__label">
        <svg
          width="10"
          height="6"
          viewBox="0 0 10 6"
          fill="none"
          xmlns="http://www.w3.org/2000/svg">
          <path
            d="M10 5C10 5.16927 9.93815 5.31576 9.81445 5.43945C9.69075 5.56315 9.54427 5.625 9.375 5.625H0.625C0.455729 5.625 0.309245 5.56315 0.185547 5.43945C0.061849 5.31576 0 5.16927 0 5C0 4.83073 0.061849 4.68424 0.185547 4.56055L4.56055 0.185547C4.68424 0.061849 4.83073 0 5 0C5.16927 0 5.31576 0.061849 5.43945 0.185547L9.81445 4.56055C9.93815 4.68424 10 4.83073 10 5Z"
            fill="#2C2C2C"
          />
        </svg>
        <b>Сортировка по:</b>
        <span onClick={() => setIsOpenSort(!isOpenSort)}>{sort.title}</span>
      </div>
      {isOpenSort && (
        <div className="sort__popup">
          <ul>
            {sortItems.map((sortItem) => (
              <li
                key={sortItem.id}
                onClick={() => closeSortWindow(sortItem)}
                className={sortItem.type === sort.type ? 'active' : ''}>
                {sortItem.title}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
