import React, { useEffect, useState } from 'react';
import './scss/app.scss';
import Header from '../src/components/Header.jsx';
import Categories from '../src/components/Categories.jsx';
import Sort from '../src/components/Sort.jsx';
import PizzaBlock from '../src/components/PizzaBlock.jsx';

function App() {
  const [items, setItems] = useState([]);
  const [active, setActive] = useState(false);

  const fetchData = async () => {
    setActive(true);
    await fetch('https://650ec8a054d18aabfe9976df.mockapi.io/items')
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setActive(false);
        setItems(data);
      });
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="wrapper">
      <Header />
      <div className="content">
        <div className="container">
          <div className="content__top">
            <Categories />
            <Sort />
          </div>
          <h2 className="content__title">Все пиццы</h2>
          <div className="content__items">
            {items.map((pizza) => (
              <PizzaBlock key={pizza.id} {...pizza} active={active} />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
