import React, { useEffect, useState } from 'react';
import Categories from '../components/Categories';
import Sort from '../components/Sort.jsx';
import PizzaBlock from '../components/PizzaBlock';
import Skeleton from '../components/PizzaBlock/Skeleton';

export default function Home() {
  const [items, setItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [category, setCategory] = React.useState(1);
  const [sort, setSort] = useState(0);

  const url = new URL('https://650ec8a054d18aabfe9976df.mockapi.io/items');

  // const sortItems = (type, categoryValue = '') => {
  //   url.searchParams.append('sortBy', type);
  //   if (sort.toString() !== '') {
  //     url.searchParams.append('category', sort.toString());
  //   }
  //   url.searchParams.append('order', 'asc');
  //   console.log(url);
  //   fetch(url, {
  //     method: 'GET',
  //     headers: { 'content-type': 'application/json' },
  //   })
  //     .then((res) => {
  //       if (res.ok) {
  //         console.log(res);
  //         return res.json();
  //       }
  //     })
  //     .then((data) => {
  //       setItems(data);
  //     });
  // };

  const fetchData = async () => {
    await fetch('https://650ec8a054d18aabfe9976df.mockapi.io/items?category=' + category)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setItems(data);
        setIsLoading(false);
      });
  };

  useEffect(() => {
    fetchData();
    window.scrollTo(0, 0);
  }, [category]);

  return (
    <div className="container">
      <div className="content__top">
        <Categories category={category} handleCategory={(i) => setCategory(i)} />
        <Sort sort={sort} />
      </div>
      <h2 className="content__title">Все пиццы</h2>
      <div className="content__items">
        {isLoading
          ? [...new Array(6)].map((_, index) => <Skeleton key={index} />)
          : items.map((item) => <PizzaBlock key={item.id} {...item} />)}
      </div>
    </div>
  );
}
