import { createSlice } from '@reduxjs/toolkit';

const initialState = [
  { id: 1, title: 'популярности (высокая оценка)', type: '+rating' },
  { id: 2, title: 'популярности (низкая оценка)', type: 'rating' },
  { id: 3, title: 'цене (по возрастанию)', type: '+price' },
  { id: 4, title: 'цене (по убыванию)', type: 'price' },
  { id: 5, title: 'алфавиту (а-я)', type: '+title' },
  { id: 6, title: 'алфавиту (я-а)', type: 'title' },
];

export const sortSlice = createSlice({
  name: 'sort', //название
  initialState, //превоначальное состояние
  //методы меняющие состояние
  reducers: {
    setActiveCategory: (state, action) => {
      const categoryId = action.payload + 1;
      state.forEach((category) => {
        //В state сейчас массив и теперь данный массив перебирается и выбирает перменную подходящую под условие
        category.active = category.id === categoryId;
      });
    },
  },
});
