import React, { useContext, useEffect, useState } from 'react';
import Categories from '../components/Categories';
import Sort from '../components/Sort.jsx';
import PizzaBlock from '../components/PizzaBlock';
import Skeleton from '../components/PizzaBlock/Skeleton';
import { SearchContext } from 'context/SearchContext';
import Pagination from 'components/Pagination';
import { useSelector } from 'react-redux';

// мой вариант поиска
// const [filterItems, setFilterItems] = useState([]);
// const [isError, setIsError] = useState(false);
// const handleSearchItems = () => {
//   if (searchValue.trim() !== '') {
//     const filteredItems = items.filter((el) =>
//       el.title.toLowerCase().includes(searchValue.toLowerCase()),
//     );
//     if (filteredItems.length > 0) {
//       setFilterItems(filteredItems);
//       setIsError(false);
//     } else {
//       setIsError(true);
//       setFilterItems(items);
//     }
//   } else {
//     setIsError(false);
//     setFilterItems(items);
//   }
// };

// useEffect(() => {
//   handleSearchItems();
// }, [searchValue]);

// setFilterItems(data);

// const pizzas = filterItems.map((item) => <PizzaBlock key={item.id} {...item} />);

// {isLoading ? skeletons : <>{!isError ? pizzas : 'По вашему запросу ничего не неайдено'}</>}

// мой вариант поиска

export default function Home() {
  const categoryId = useSelector((state) => state.filter.categoryId);
  const [items, setItems] = useState([]);
  const { searchValue } = useContext(SearchContext);
  const [isLoading, setIsLoading] = useState(true);
  const activeCategory = 0;
  // const activeCategory = useSelector((state) =>
  //   state.dataManagment.find((category) => category.active),
  // );

  const [currentPage, setCurrentPage] = React.useState(1);
  const [sort, setSort] = useState({
    title: 'популярности',
    type: 'rating',
  });

  const fetchData = async () => {
    setIsLoading(true);
    const url = 'https://650ec8a054d18aabfe9976df.mockapi.io/items';
    const replaceSortType = sort.type.replace('+', '');
    const replaceSortTypeFirstSymbol = sort.type.startsWith('+') ? 'asc' : 'desc';
    // const categorySort = activeCategory.id - 1 > 0 ? `category=${activeCategory.id}` : '';
    const categorySort = 1;
    const searchParam = searchValue ? `&search=${searchValue}` : ''; //добавил параметр для поиска
    //TODO - переделать выпадающий список в сортировке, добавить икони вверх вниз, вместо строчек

    await fetch(
      `${url}?p=${currentPage}&l=4&${categorySort}&sortBy=${replaceSortType}&order=${replaceSortTypeFirstSymbol}${searchParam}`,
    )
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setItems(data);
        setIsLoading(false);
      });
  };

  useEffect(() => {
    fetchData();
  }, [activeCategory, sort, searchValue, currentPage]);

  const skeletons = [...new Array(6)].map((_, index) => <Skeleton key={index} />);
  const pizzas = items.map((item) => <PizzaBlock key={item.id} {...item} />);

  return (
    <div className="container">
      <div className="content__top">
        <Categories value={categoryId} onChangeCategory={(i) => console.log(i)} />
        <Sort />
      </div>
      <h2 className="content__title">Все пиццы</h2>
      <div className="content__items">
        {isLoading ? (
          skeletons
        ) : (
          <>
            {pizzas.length ? (
              pizzas
            ) : (
              <p style={{ marginBottom: '16px' }}>По вашему запросу ничего не найдено </p>
            )}
          </>
        )}
      </div>
      <Pagination onPageChange={(number) => setCurrentPage(number)} />
    </div>
  );
}
