import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
//useSelector - некий аналог useContext
//useDispatch - хук для выполнения какого-то действия
import { setActiveCategory } from '../redux/slices/sortingSlice'; //импорт функции

export default function Categories({ category }) {
  const categoriesItems = useSelector((state) => state.dataManagment);
  const dispatch = useDispatch();
  return (
    <div className="categories">
      <ul>
        {categoriesItems.map((value, index) => (
          <li
            key={value.id}
            onClick={() => dispatch(setActiveCategory(index))}
            className={value.active ? 'active' : ''}>
            {value.title}
          </li>
        ))}
      </ul>
    </div>
  );
}
