import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { selectFilter } from '../redux/slices/filterSlice';

export default function Categories({ categoryId, onChangeCategory }) {
  const dispatch = useDispatch();
  const { setCategoryId } = useSelector(selectFilter);
  console.log(setCategoryId);
  const categoriesItems = [
    { id: 1, title: 'Все', active: true },
    { id: 2, title: 'Мясные', active: false },
    { id: 3, title: 'Вегетарианская', active: false },
    { id: 4, title: 'Гриль', active: false },
    { id: 5, title: 'Острые', active: false },
    { id: 6, title: 'Закрытые', active: false },
  ];

  // console.log(categoryId);
  return (
    <div className="categories">
      <ul>
        {categoriesItems.map((value, index) => (
          <li
            key={value.id}
            onClick={() => dispatch(setCategoryId(value.id - 1))}
            className={value.id - 1 === categoryId ? 'active' : ''}>
            {value.title}
          </li>
        ))}
      </ul>
    </div>
  );
}
