import { createSlice } from '@reduxjs/toolkit';

const initialState = [
  { id: 1, title: 'Все', active: true },
  { id: 2, title: 'Мясные', active: false },
  { id: 3, title: 'Вегетарианская', active: false },
  { id: 4, title: 'Гриль', active: false },
  { id: 5, title: 'Острые', active: false },
  { id: 6, title: 'Закрытые', active: false },
]; //Первоначальное состояние(можно писать все что угодно)

//функция создаст класс с настройками, а также в ней будет храниться логика для работы с массивом
export const categoriesSlice = createSlice({
  name: 'categories', //название
  initialState, //превоначальное состояние
  reducers: {
    setActiveCategory: (state, action) => {
      const categoryId = action.payload + 1;
      state.forEach((category) => {
        //В state сейчас массив и теперь данный массив перебирается и выбирает перменную подходящую под условие
        category.active = category.id === categoryId;
      });
    },
  },
});

export const { setActiveCategory } = categoriesSlice.actions; //categoriesSlice вернет все actions, в данном случае вернет только setActiveCategory
export default categoriesSlice.reducer; //делает экспорт функции, которую можно будет использовать в других файлах
